function(frame, time) {
  const width = frame.width;
  const height = frame.height;

  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      // Mehrere Sinuswellen fÃ¼r Aurora-VorhÃ¤nge
      const curtain1 = Math.sin(y * 0.15 + time * 1.5 + x * 0.1) * 0.5;
      const curtain2 = Math.sin(y * 0.25 - time * 1.0 + x * 0.15) * 0.3;
      const curtain3 = Math.sin(y * 0.35 + time * 2.0 + x * 0.08) * 0.2;

      const intensity = (curtain1 + curtain2 + curtain3 + 1.0) / 2.0;

      // Farbverlauf von GrÃ¼n zu Violett
      const colorShift = Math.sin(x * 0.2 + time * 0.5) * 0.5 + 0.5;

      let r, g, b;
      if (colorShift < 0.5) {
        // GrÃ¼ntÃ¶ne (klassisches Nordlicht)
        r = Math.floor(intensity * 50 * (1 - colorShift * 2));
        g = Math.floor(intensity * 255);
        b = Math.floor(intensity * 100);
      } else {
        // Violett/Pink-TÃ¶ne
        const violet = (colorShift - 0.5) * 2;
        r = Math.floor(intensity * (100 + violet * 155));
        g = Math.floor(intensity * 50);
        b = Math.floor(intensity * (150 + violet * 105));
      }

      frame.setPixel(x, y, { r, g, b });
    }
  }
}
