function(frame, time) {
  const width = frame.width;
  const height = frame.height;

  // Langsames Pulsieren (4 Sekunden Zyklus)
  const breathe = (Math.sin(time * Math.PI * 0.5) + 1) / 2;

  // Sanfter Farbwechsel Ã¼ber Zeit
  const colorCycle = time * 0.1;

  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      // Radiale Distanz vom Zentrum
      const centerX = width / 2;
      const centerY = height / 2;
      const dx = (x - centerX) / width;
      const dy = (y - centerY) / height;
      const distance = Math.sqrt(dx * dx + dy * dy);

      // Pulsierender Ring-Effekt
      const ringIntensity = Math.sin(distance * 5 - time * 2) * 0.5 + 0.5;
      const intensity = breathe * ringIntensity;

      // Sanfter Farbverlauf (Blau zu Violett zu Rosa)
      const hue = (colorCycle + distance * 0.5) % 1.0;

      let r, g, b;
      if (hue < 0.33) {
        // Blau
        const t = hue * 3;
        r = Math.floor(intensity * (50 + t * 100));
        g = Math.floor(intensity * (100 - t * 50));
        b = Math.floor(intensity * 255);
      } else if (hue < 0.66) {
        // Violett
        const t = (hue - 0.33) * 3;
        r = Math.floor(intensity * (150 + t * 105));
        g = Math.floor(intensity * (50 - t * 30));
        b = Math.floor(intensity * (255 - t * 100));
      } else {
        // Rosa
        const t = (hue - 0.66) * 3;
        r = Math.floor(intensity * 255);
        g = Math.floor(intensity * (20 + t * 80));
        b = Math.floor(intensity * (155 - t * 55));
      }

      frame.setPixel(x, y, { r, g, b });
    }
  }
}
