function(frame, time) {
  const width = frame.width;
  const height = frame.height;

  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      // Feuer kommt von unten und wird nach oben schwÃ¤cher
      const heightFactor = (height - y) / height;

      // Flackern durch mehrere Noise-Schichten
      const flicker1 = Math.sin(x * 0.5 + time * 8 + y * 0.3) * 0.5;
      const flicker2 = Math.sin(x * 0.3 - time * 6 + y * 0.4) * 0.3;
      const flicker3 = Math.sin(x * 0.7 + time * 10 + y * 0.2) * 0.2;

      const flicker = (flicker1 + flicker2 + flicker3 + 1.0) / 2.0;

      // IntensitÃ¤t basierend auf HÃ¶he und Flackern
      const intensity = heightFactor * flicker;

      let r, g, b;

      if (intensity < 0.2) {
        // Sehr schwach = fast schwarz/dunkelrot
        r = Math.floor(intensity * 255);
        g = 0;
        b = 0;
      } else if (intensity < 0.5) {
        // Rot
        r = Math.floor(200 + intensity * 55);
        g = Math.floor(intensity * 100);
        b = 0;
      } else if (intensity < 0.75) {
        // Orange
        r = 255;
        g = Math.floor(100 + intensity * 155);
        b = 0;
      } else {
        // Helles Gelb-WeiÃŸ (heiÃŸeste Flammen)
        r = 255;
        g = Math.floor(200 + intensity * 55);
        b = Math.floor((intensity - 0.75) * 400);
      }

      frame.setPixel(x, y, { r, g, b });
    }
  }
}
