function(frame, time) {
  const width = frame.width;
  const height = frame.height;

  // Schwarzer Hintergrund
  frame.fill({ r: 0, g: 0, b: 0 });

  // Pseudo-Zufallsfunktion
  function pseudoRandom(seed) {
    return Math.abs(Math.sin(seed * 12.9898) * 43758.5453) % 1;
  }

  // FÃ¼r jede Spalte eine fallende Code-Zeile
  for (let x = 0; x < width; x++) {
    // Jede Spalte hat ihre eigene Geschwindigkeit und Offset
    const speed = 2 + pseudoRandom(x * 123) * 3;
    const offset = pseudoRandom(x * 456) * height * 2;

    // Position des "Kopfes" der Code-Zeile
    const headY = ((time * speed * 5) + offset) % (height + 20);

    // LÃ¤nge der Spur
    const trailLength = 8 + Math.floor(pseudoRandom(x * 789) * 10);

    for (let i = 0; i < trailLength; i++) {
      const y = Math.floor(headY - i);

      if (y >= 0 && y < height) {
        // Helligkeit nimmt ab je weiter vom Kopf entfernt
        let brightness;
        if (i === 0) {
          // Kopf ist am hellsten (weiÃŸ-grÃ¼n)
          brightness = 1.0;
          frame.setPixel(x, y, { 
            r: Math.floor(200 * brightness), 
            g: Math.floor(255 * brightness), 
            b: Math.floor(200 * brightness) 
          });
        } else {
          // Spur wird dunkler
          brightness = Math.max(0, 1 - (i / trailLength));
          frame.setPixel(x, y, { 
            r: 0, 
            g: Math.floor(255 * brightness), 
            b: 0 
          });
        }
      }
    }
  }
}
