function(frame, time) {
  const width = frame.width;
  const height = frame.height;

  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      // Mehrere Ã¼berlagerte Sinuswellen fÃ¼r realistischen Ozean-Effekt
      const wave1 = Math.sin(x * 0.3 + time * 2) * 0.5;
      const wave2 = Math.sin(x * 0.15 - time * 1.5 + y * 0.1) * 0.3;
      const wave3 = Math.sin(x * 0.45 + time * 2.5 + y * 0.05) * 0.2;

      // Kombinierte WellenhÃ¶he
      const waveHeight = (wave1 + wave2 + wave3) * height * 0.3 + height * 0.5;

      // Tiefenbasierte Farbgebung (tiefer = dunkler blau)
      const depth = (y - waveHeight) / height;

      let r, g, b;
      if (y > waveHeight) {
        // Unter Wasser - BlautÃ¶ne mit Tiefenvariation
        const blueness = Math.max(0, Math.min(1, depth * 2));
        r = Math.floor(0 + blueness * 20);
        g = Math.floor(50 + blueness * 80);
        b = Math.floor(120 + blueness * 135);
      } else {
        // Ãœber Wasser - helles TÃ¼rkis/Schaum
        const foam = Math.max(0, 1 - depth * 5);
        r = Math.floor(100 + foam * 155);
        g = Math.floor(200 + foam * 55);
        b = Math.floor(220 + foam * 35);
      }

      frame.setPixel(x, y, { r, g, b });
    }
  }
}
