function(frame, time) {
  const width = frame.width;
  const height = frame.height;

  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      // Klassischer Plasma-Effekt mit mehreren Sinuswellen
      const value1 = Math.sin(x / 5.0 + time);
      const value2 = Math.sin(y / 4.0 - time * 1.2);
      const value3 = Math.sin((x + y) / 6.0 + time * 0.8);
      const value4 = Math.sin(Math.sqrt(x * x + y * y) / 4.0 - time * 1.5);

      // Kombiniere die Werte
      const plasma = (value1 + value2 + value3 + value4) / 4.0;

      // Konvertiere zu Farbwert (0-1)
      const normalized = (plasma + 1.0) / 2.0;

      // HSV zu RGB Konvertierung fÃ¼r Regenbogen-Plasma
      const hue = (normalized + time * 0.1) % 1.0;

      let r, g, b;
      const h = hue * 6;
      const c = 255;
      const x_val = c * (1 - Math.abs((h % 2) - 1));

      if (h < 1) {
        r = c; g = x_val; b = 0;
      } else if (h < 2) {
        r = x_val; g = c; b = 0;
      } else if (h < 3) {
        r = 0; g = c; b = x_val;
      } else if (h < 4) {
        r = 0; g = x_val; b = c;
      } else if (h < 5) {
        r = x_val; g = 0; b = c;
      } else {
        r = c; g = 0; b = x_val;
      }

      frame.setPixel(x, y, { r: Math.floor(r), g: Math.floor(g), b: Math.floor(b) });
    }
  }
}
