function(frame, time) {
  const width = frame.width;
  const height = frame.height;

  // Dunkler, bewÃ¶lkter Himmel
  frame.fill({ r: 10, g: 15, b: 30 });

  // Pseudo-Zufallsfunktion
  function pseudoRandom(seed) {
    return Math.abs(Math.sin(seed * 12.9898) * 43758.5453) % 1;
  }

  // Mehrere Regenspuren
  const numDrops = 30;

  for (let i = 0; i < numDrops; i++) {
    // Jeder Tropfen hat eine feste X-Position und Geschwindigkeit
    const dropX = Math.floor(pseudoRandom(i * 123) * width);
    const dropSpeed = 3 + pseudoRandom(i * 456) * 2;

    // Y-Position basierend auf Zeit (Loop)
    const dropY = ((time * dropSpeed * 10) + pseudoRandom(i * 789) * height) % (height + 10);

    // TropfenlÃ¤nge
    const dropLength = 3 + Math.floor(pseudoRandom(i * 321) * 3);

    // Zeichne Regentropfen (von hell nach dunkel)
    for (let j = 0; j < dropLength; j++) {
      const y = Math.floor(dropY - j);

      if (y >= 0 && y < height) {
        const brightness = 1 - (j / dropLength);
        const r = Math.floor(100 * brightness);
        const g = Math.floor(150 * brightness);
        const b = Math.floor(255 * brightness);

        frame.setPixel(dropX, y, { r, g, b });
      }
    }
  }
}
