function(frame, time) {
  const width = frame.width;
  const height = frame.height;

  // Schwarzer Hintergrund
  frame.clear();

  // Pseudo-Zufallsfunktion basierend auf Position
  function pseudoRandom(x, y, seed) {
    return Math.abs(Math.sin(x * 12.9898 + y * 78.233 + seed) * 43758.5453) % 1;
  }

  // Sterne generieren
  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      const starChance = pseudoRandom(x, y, 42);

      // Nur 8% der Pixel sind Sterne
      if (starChance > 0.92) {
        // Langsames Funkeln basierend auf Position und Zeit
        const twinkleSpeed = pseudoRandom(x, y, 123) * 3 + 1;
        const twinklePhase = pseudoRandom(x, y, 456) * 6.28;
        const brightness = (Math.sin(time * twinkleSpeed + twinklePhase) + 1) / 2;

        // Sternfarbe (meistens weiß, manchmal leicht bläulich oder gelblich)
        const colorVariation = pseudoRandom(x, y, 789);
        let r, g, b;

        if (colorVariation < 0.7) {
          // Weiße Sterne
          r = g = b = Math.floor(brightness * 255);
        } else if (colorVariation < 0.85) {
          // Bläuliche Sterne
          r = Math.floor(brightness * 200);
          g = Math.floor(brightness * 220);
          b = Math.floor(brightness * 255);
        } else {
          // Gelbliche Sterne
          r = Math.floor(brightness * 255);
          g = Math.floor(brightness * 240);
          b = Math.floor(brightness * 180);
        }

        frame.setPixel(x, y, { r, g, b });
      }
    }
  }
}
