function(frame, time) {
  const width = frame.width;
  const height = frame.height;

  // Langsame Bewegung des Sonnenuntergangs
  const cycle = time * 0.05;
  const sunProgress = (Math.sin(cycle) + 1) / 2;

  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      // Vertikaler Gradient von oben (Himmel) nach unten (Horizont)
      const verticalPos = y / height;

      // Sonne Position
      const sunY = height * 0.3 + sunProgress * height * 0.4;
      const sunX = width / 2 + Math.sin(cycle * 2) * width * 0.3;

      const dx = x - sunX;
      const dy = y - sunY;
      const distToSun = Math.sqrt(dx * dx + dy * dy);

      let r, g, b;

      // Sonne (heller Kreis)
      if (distToSun < 3) {
        r = 255;
        g = 250;
        b = 200;
      } else {
        // Himmelsgradient
        if (verticalPos < 0.3) {
          // Oberer Himmel (dunkelblau zu orange)
          const t = verticalPos / 0.3;
          r = Math.floor(20 + t * 235);
          g = Math.floor(30 + t * 120);
          b = Math.floor(80 + t * 50);
        } else if (verticalPos < 0.5) {
          // Horizont (orange zu gelb)
          const t = (verticalPos - 0.3) / 0.2;
          r = Math.floor(255 - t * 30);
          g = Math.floor(150 + t * 80);
          b = Math.floor(30 - t * 20);
        } else if (verticalPos < 0.7) {
          // Unterer Horizont (gelb zu rot-orange)
          const t = (verticalPos - 0.5) / 0.2;
          r = Math.floor(225 + t * 30);
          g = Math.floor(230 - t * 130);
          b = Math.floor(10 + t * 40);
        } else {
          // Boden/Wasser (dunkel)
          const t = (verticalPos - 0.7) / 0.3;
          r = Math.floor(255 - t * 235);
          g = Math.floor(100 - t * 80);
          b = Math.floor(50 - t * 40);
        }

        // Sonnenstrahlen-Effekt
        const rayIntensity = Math.max(0, 1 - distToSun / 15) * 0.3;
        r = Math.min(255, Math.floor(r + rayIntensity * 100));
        g = Math.min(255, Math.floor(g + rayIntensity * 80));
        b = Math.min(255, Math.floor(b + rayIntensity * 30));
      }

      frame.setPixel(x, y, { r, g, b });
    }
  }
}
